package javaBasics;

// keyword should always start with small letter, Class should start with capital letter
// Every statement should End with semicolon

public class DataTypes {	

		//main() method - Starting/Execution point of the Program
		public static void main(String[] args) {
		// TODO Auto-generated method stub
			
			//Primitive Data Types : int, double, char, boolean
			
			// 1. int:
			int i1=10;
			i1 = 20;
			int i2 = -5;
			int i3 = 0;
			System.out.println(i3);
			
			// 2. double:
			double d1 = 12.33;
			double d2 = -5.89;
			double d3 = 50; //50.00
			System.out.println(d2);
			
			// 3. char: only single digit values in single inverted comma
			char c1 = 'q';
			char c2 = 'Q';
			char c3 = '1'; 
			char c4 = '$';
			System.out.println(c4);
			
			//4. boolean:
			boolean b1 = true;
			boolean b2 = false;
			/////////*************Interview Que - 'true' and 'false' are Keyword as well as Value depending upon the situation.*******///////
			
			//5. String(Non-primitive Data type): is a Class, not DataType(but we can use it as data type for String values)
			String s1 = "Panda";
			String s2 = "Hello welcome to java training";
			String s3 = "1000";
			String s4 = "25.7888";
			System.out.println(s2);
			
			

	}

}
